import { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';

import Candidate from '../components/CandidateCard';
import CandidateForm from '../components/CandidateForm';

export default function Admin({ role, contract, web3, currentAccount }) {
	const [electionState, setElectionState] = useState(0);
	const [loading, setLoading] = useState(true);
	const [candidates, setCandidates] = useState([]);

	const [open, setOpen] = useState(false);

	const getCandidates = async () => {
		if (contract) {
			const count = await contract.methods.candidatesCount().call();
			const temp = [];
			for (let i = 0; i < count; i++) {
				const candidate = await contract.methods
					.getCandidateDetails(i)
					.call();
				temp.push({ name: candidate[0], votes: candidate[1] });
			}
			setCandidates(temp);
			setLoading(false);
		}
	};

	const getElectionState = async () => {
		if (contract) {
			const state = await contract.methods.electionState().call();
			setElectionState(parseInt(state));
		}
	};

	useEffect(() => {
		getElectionState();
		getCandidates();
	}, [contract]);

	const handleEnd = () => {
		setOpen(true);
	};

	const handleClose = () => {
		setOpen(false);
	};

	const handleAgree = async () => {
		if (electionState === 0) {
			try {
				if (contract) {
					await contract.methods
						.startElection()
						.send({ from: currentAccount });
					getElectionState();
				}
			} catch (error) {
				console.error('Error:', error);
			}
		} else if (electionState === 1) {
			try {
				if (contract) {
					await contract.methods
						.endElection()
						.send({ from: currentAccount });
					getElectionState();
				}
			} catch (error) {
				console.error('Error:', error);
			}
		}

		setOpen(false);
	};

	return (
		<Box>
			{loading ? (
				<Box
					sx={{
						display: 'flex',
						justifyContent: 'center',
						alignItems: 'center',
						height: '80vh',
					}}
				>
					Loading...
				</Box>
			) : (
				<Box>
					<Grid container sx={{ mt: 0 }} spacing={4}>
						<Grid item xs={12}>
							<Typography
								align="center"
								variant="h6"
								color="textSecondary"
							>
								ELECTION STATUS :{' '}
								{electionState === 0 &&
									'Election has not started.'}
								{electionState === 1 &&
									'Election is in progress.'}
								{electionState === 2 && 'Election has ended.'}
							</Typography>
							<Divider />
						</Grid>
						{electionState !== 2 && (
							<Grid item xs={12} sx={{ display: 'flex' }}>
								<Button
									variant="contained"
									sx={{ width: '40%', margin: 'auto' }}
									onClick={handleEnd}
								>
									{electionState === 0 && 'Start Election'}
									{electionState === 1 && 'End Election'}
								</Button>
							</Grid>
						)}

						<Grid item xs={12}>
							<Typography align="center" variant="h6">
								{electionState === 1 && 'SEE LIVE RESULTS'}
								{electionState === 2 && 'FINAL ELECTION RESULT'}
							</Typography>
							<Divider />
						</Grid>

						{electionState === 0 && (
							<Grid
								item
								xs={12}
								sx={{
									overflowY: 'hidden',
									overflowX: 'auto',
									display: 'flex',
									width: '98vw',
									justifyContent: 'center',
								}}
							>
								<Box
									sx={{
										display: 'flex',
										flexDirection: 'column',
										width: '100%',
										alignItems: 'center',
									}}
								>
									<CandidateForm
										contract={contract}
										web3={web3}
										currentAccount={currentAccount}
									/>
								</Box>
							</Grid>
						)}

						{electionState > 0 && (
							<Grid
								item
								xs={12}
								sx={{
									overflowY: 'hidden',
									overflowX: 'auto',
									display: 'flex',
									width: '98vw',
									justifyContent: 'center',
								}}
							>
								{candidates &&
									candidates.map((candidate, index) => (
										<Box sx={{ mx: 2 }} key={index}>
											<Candidate
												id={index}
												name={candidate.name}
												voteCount={candidate.votes}
											/>
										</Box>
									))}
							</Grid>
						)}
					</Grid>

					<Dialog
						open={open}
						onClose={handleClose}
						aria-labelledby="alert-dialog-title"
						aria-describedby="alert-dialog-description"
					>
						<DialogContent>
							<DialogContentText id="alert-dialog-description">
								{electionState === 0 &&
									'Do you want to start the election?'}
								{electionState === 1 &&
									'Do you want to end the election?'}
							</DialogContentText>
						</DialogContent>
						<DialogActions>
							<Button onClick={handleClose}>Disagree</Button>
							<Button onClick={handleAgree} autoFocus>
								Agree
							</Button>
						</DialogActions>
					</Dialog>
				</Box>
			)}
		</Box>
	);
}
